-- used to precompute daData
precompute = map (\(i,j) -> (ceiling i, ceiling j)) (filter
                     (\(n,m) -> n <= 3000.0)
                     (filter
                      (\(a,b) -> a /= b)
                      (map
                       (\x-> (sum (iii x),product (iii x)))
                       [1.0 .. 3000.0])))

iii n = (n:(hhh n))

hhh n = filter (\x-> (floor x) == (ceiling x)) (ggg n)

ggg n = map (\x -> sqrt ( n^2 - x^2)) [ n-1, (n-2) .. (n / 2)]

pyth n = if (null (ddd n)) then (-1) else maximum (map (\(_,b) -> b) (ddd n))
  where ddd n = filter (\(a,b) -> a == n) daData

readInteger :: IO Integer
readInteger = getLine >>= (\s -> return (read s :: Integer))

readNUmbers :: Integer -> IO [Integer]
readNUmbers n = forM [1..n] (\_ -> readInteger)

main :: IO ()
main = do
  ri <- readInteger
  -- putStrLn ("read "++ (show ri))
  zz <- readNUmbers ri
  mapM_ (\n -> putStrLn (show (pyth n))) zz
